pub fn setup_profiles(
    h: &Handler,
    instances: &Vec<Instance>,
) -> Result<(), Box<dyn std::error::Error>> {
    println!("\n[splitux] Instances:");
    for instance in instances {
        if instance.profname.starts_with(".") {
            create_profile(&instance.profname)?;
        }
        if h.is_saved_handler() {
            create_profile_gamesave(&instance.profname, h)?;
        }
        println!(
            "[splitux] - Profile: {}, Monitor: {}, Resolution: {}x{}",
            instance.profname, instance.monitor, instance.width, instance.height
        );
    }

    Ok(())
}

pub fn launch_game(
    h: &Handler,
    input_devices: &[DeviceInfo],
    instances: &Vec<Instance>,
    monitors: &[Monitor],
    cfg: &PartyConfig,
) -> Result<(), Box<dyn std::error::Error>> {
    let new_cmds = launch_cmds(h, input_devices, instances, cfg)?;
    print_launch_cmds(&new_cmds);

    // Create WM backend based on config
    let mut wm = match &cfg.window_manager {
        WindowManagerType::Auto => WindowManagerBackend::detect(),
        WindowManagerType::KWin => WindowManagerBackend::KWin(crate::wm::KWinManager::new()),
        WindowManagerType::Hyprland => {
            WindowManagerBackend::Hyprland(crate::wm::HyprlandManager::new())
        }
        WindowManagerType::GamescopeOnly => {
            WindowManagerBackend::GamescopeOnly(crate::wm::GamescopeOnlyManager::new())
        }
    };

    // Setup WM with layout context
    let orientation = if cfg.vertical_two_player {
        LayoutOrientation::Vertical
    } else {
        LayoutOrientation::Horizontal
    };

    let ctx = LayoutContext {
        instances: instances.clone(),
        monitors: monitors.to_vec(),
        orientation,
    };

    println!("[splitux] Setting up {} window manager", wm.name());
    wm.setup(&ctx)?;

    let sleep_time = match h.pause_between_starts {
        Some(f) => f,
        None => 0.5,
    };

    let mut handles = Vec::new();

    let mut i = 0;
    for mut cmd in new_cmds {
        let handle = cmd.spawn()?;
        handles.push(handle);

        if i < instances.len() - 1 {
            std::thread::sleep(std::time::Duration::from_secs_f64(sleep_time));
        }
        i += 1;
    }

    // Notify WM that all instances have been launched (for positioning)
    // Only non-reactive WMs (like Hyprland) need explicit positioning
    if !wm.is_reactive() {
        println!("[splitux] Non-reactive WM, positioning windows explicitly");
        wm.on_instances_launched(&ctx)?;
    }

    for mut handle in handles {
        handle.wait()?;
    }

    // Teardown WM
    println!("[splitux] Tearing down {} window manager", wm.name());
    wm.teardown()?;

    Ok(())
}

pub fn launch_cmds(
    h: &Handler,
    input_devices: &[DeviceInfo],
    instances: &Vec<Instance>,
    cfg: &PartyConfig,
) -> Result<Vec<std::process::Command>, Box<dyn std::error::Error>> {
    let win = h.win();
    let exec = Path::new(&h.exec);
    let runtime = h.runtime.as_str();

    // Validate Steam Runtime if needed
    if (runtime == "scout" && !PATH_STEAM.join("bin32/steam-runtime/run.sh").exists())
        || (runtime == "soldier"
            && !PATH_STEAM
                .join("steam/steamapps/common/SteamLinuxRuntime_soldier")
                .exists())
    {
        return Err(format!("Steam Runtime {runtime} not found!").into());
    }

    // Create Goldberg overlays if needed (before mounting game dirs)
    let goldberg_overlays = if h.use_goldberg && h.is_saved_handler() {
        let game_root = PathBuf::from(h.get_game_rootpath()?);
        let dlls = goldberg::find_steam_api_dlls(&game_root)?;

        if dlls.is_empty() {
            println!("[splitux] Warning: use_goldberg enabled but no Steam API DLLs found");
            vec![]
        } else {
            // Generate unique ports for each instance using instance index
            // This guarantees no port collisions even if profile names hash to the same value
            const BASE_PORT: u16 = 47584;
            let instance_ports: Vec<u16> = (0..instances.len())
                .map(|i| BASE_PORT + i as u16)
                .collect();

            // Build configs for each instance
            let configs: Vec<GoldbergConfig> = instances
                .iter()
                .enumerate()
                .map(|(i, instance)| {
                    // Broadcast ports = all other instances' ports
                    let broadcast_ports: Vec<u16> = instance_ports
                        .iter()
                        .enumerate()
                        .filter(|(j, _)| *j != i)
                        .map(|(_, &port)| port)
                        .collect();

                    GoldbergConfig {
                        app_id: h.steam_appid.unwrap_or(480),
                        steam_id: generate_steam_id(&instance.profname),
                        account_name: instance.profname.clone(),
                        listen_port: instance_ports[i],
                        broadcast_ports,
                    }
                })
                .collect();

            goldberg::create_all_overlays(&dlls, &configs, win)?
        }
    } else {
        vec![]
    };

    // Mount game directories with overlays
    if h.is_saved_handler() && !cfg.disable_mount_gamedirs {
        fuse_overlayfs_mount_gamedirs(h, instances, &goldberg_overlays)?;
    }

    let mut cmds: Vec<Command> = Vec::new();

    for (i, instance) in instances.iter().enumerate() {
        let gamedir = if h.is_saved_handler() && !cfg.disable_mount_gamedirs {
            PATH_PARTY.join("tmp").join(format!("game-{}", i))
        } else {
            PathBuf::from(h.get_game_rootpath()?)
        };

        if !gamedir.join(exec).exists() {
            return Err(format!("Executable not found: {}", gamedir.join(exec).display()).into());
        }

        let path_exec = gamedir.join(exec);
        let cwd = path_exec.parent().ok_or_else(|| "couldn't get parent")?;
        let path_prof = PATH_PARTY.join("profiles").join(&instance.profname);

        // 1. Create gamescope command
        let mut cmd = gamescope::create_command(cfg);
        cmd.current_dir(cwd);

        // 2. Set up gamescope environment
        gamescope::setup_env(&mut cmd);

        // Proton debug logging
        cmd.env("PROTON_LOG", "1");
        cmd.env("WINEDEBUG", "trace+dinput,trace+xinput");
        cmd.env("PROTON_USE_XALIA", "0");

        // SDL2 override if configured
        if h.sdl2_override != SDL2Override::No {
            let path_sdl = match h.sdl2_override {
                SDL2Override::Srt => {
                    PATH_STEAM.join("bin32/steam-runtime/usr/lib/i386-linux-gnu/libSDL2-2.0.so.0")
                }
                SDL2Override::Sys => PathBuf::from("/usr/lib/libSDL2.so"),
                _ => PathBuf::new(),
            };
            cmd.env("SDL_DYNAMIC_API", path_sdl);
        }

        // Proton environment (for Windows games)
        if win {
            proton::setup_env(&mut cmd, h, cfg, i);
        }

        // Steam Input configuration
        if cfg.pad_filter_type != PadFilterType::NoSteamInput {
            cmd.env("SDL_GAMECONTROLLER_ALLOW_STEAM_VIRTUAL_GAMEPAD", "1");
        }
        if cfg.pad_filter_type == PadFilterType::OnlySteamInput {
            cmd.env("SDL_GAMECONTROLLER_IGNORE_DEVICES", SDL_GAMECONTROLLER_IGNORE_DEVICES);
        }

        // Handler custom environment variables
        if !h.env.is_empty() {
            for env_var in h.env.split_whitespace() {
                if let Some((key, value)) = env_var.split_once('=') {
                    cmd.env(key, value);
                }
            }
        }

        // 3. Add gamescope arguments
        gamescope::add_args(&mut cmd, instance, cfg);
        gamescope::add_kbm_args(&mut cmd, instance, input_devices, cfg);
        gamescope::add_separator(&mut cmd);

        // 4. Add bwrap container
        bwrap::add_base_args(&mut cmd);

        // Get gamepad paths for this instance
        let gamepad_paths = bwrap::get_assigned_gamepad_paths(input_devices, &instance.devices);
        if !gamepad_paths.is_empty() {
            println!("[splitux] Instance {}: SDL_JOYSTICK_DEVICE={}", i, gamepad_paths.join(","));
        }

        // Set up SDL environment inside container
        bwrap::setup_sdl_env(&mut cmd, &gamepad_paths);

        // Block unassigned input devices
        bwrap::block_unassigned_devices(&mut cmd, input_devices, &instance.devices, i);

        // 5. Profile bindings
        if win {
            let path_pfx_user = proton::get_prefix_user_path(cfg, i);
            cmd.arg("--bind")
                .args([&path_prof.join("windata"), &path_pfx_user]);
        } else {
            let path_prof_home = path_prof.join("home");
            cmd.env("HOME", &path_prof_home);
            // Also bind the Steam directory as the Steam runtimes look for HOME/.steam
            if !runtime.is_empty() || h.steam_appid.is_some() {
                cmd.args([
                    "--bind",
                    &PATH_STEAM.to_string_lossy(),
                    &path_prof_home.join(".steam").to_string_lossy(),
                ]);
            }
        }

        // 6. Game null paths (disable specific game features)
        for subpath in &h.game_null_paths {
            let game_subpath = gamedir.join(subpath);
            if game_subpath.is_file() {
                cmd.args(["--bind", "/dev/null", &game_subpath.to_string_lossy()]);
            } else if game_subpath.is_dir() {
                cmd.args([
                    "--bind",
                    &PATH_PARTY.join("tmp/null").to_string_lossy(),
                    &game_subpath.to_string_lossy(),
                ]);
            }
        }

        // 7. Runtime (Proton/Wine or Steam Runtime)
        if win {
            let proton_bin = proton::get_binary(h)?;
            cmd.arg(&proton_bin);

            // Add waitforexitandrun only for direct Proton (not umu-run)
            if proton::uses_direct_proton(h) {
                cmd.arg("waitforexitandrun");
            }
        } else {
            match runtime {
                "scout" => {
                    cmd.arg(PATH_STEAM.join("bin32/steam-runtime/run.sh"));
                }
                "soldier" => {
                    cmd.arg(
                        PATH_STEAM.join(
                            "steam/steamapps/common/SteamLinuxRuntime_soldier/_v2-entry-point",
                        ),
                    );
                    cmd.arg("--");
                }
                _ => {}
            };
        }

        // 8. Game executable
        cmd.arg(&path_exec);

        // 9. Handler arguments with variable substitution
        for arg in h.args.split_whitespace() {
            let processed_arg = match arg {
                "$PROFILE" => &instance.profname,
                "$WIDTH" => &instance.width.to_string(),
                "$HEIGHT" => &instance.height.to_string(),
                "$RESOLUTION" => &format!("{}x{}", instance.width, instance.height),
                "$INSTANCECOUNT" => &instances.len().to_string(),
                "$INSTANCENUM" => &i.to_string(),
                "$GAMEDIR" => &gamedir.os_fmt(win),
                "$HANDLERDIR" => &h.path_handler.os_fmt(win),
                _ => &String::from(arg).sanitize_path(),
            };
            cmd.arg(processed_arg);
        }

        cmds.push(cmd);
    }

    Ok(cmds)
}

fn print_launch_cmds(cmds: &Vec<Command>) {
    for (i, cmd) in cmds.iter().enumerate() {
        println!("[splitux] INSTANCE {}:", i + 1);

        let cwd = cmd.get_current_dir().unwrap_or_else(|| Path::new(""));
        println!("[splitux] CWD={}", cwd.display());

        for var in cmd.get_envs() {
            let value = var.1.ok_or_else(|| "").unwrap_or_default();
            println!(
                "[splitux] {}={}",
                var.0.to_string_lossy(),
                value.display()
            );
        }

        println!("[splitux] \"{}\"", cmd.get_program().display());

        print!("[splitux] ");
        for arg in cmd.get_args() {
            let fmtarg = arg.to_string_lossy();
            if fmtarg == "--bind"
                || fmtarg == "bwrap"
                || (fmtarg.starts_with("/") && fmtarg.len() > 1)
            {
                print!("\n[splitux] ");
            } else {
                print!(" ");
            }
            print!("\"{}\"", fmtarg);
        }

        println!("\n[splitux] ---------------------");
    }
}

/// Mount game directories with fuse-overlayfs
///
/// Creates overlay mounts for each instance with:
/// 1. Goldberg overlay (if enabled) - replaces Steam API DLLs
/// 2. Handler overlay (if exists) - custom handler files
/// 3. Base game directory - read-only game files
/// 4. Upper dir - per-profile save data (read-write)
pub fn fuse_overlayfs_mount_gamedirs(
    h: &Handler,
    instances: &Vec<Instance>,
    goldberg_overlays: &[PathBuf],
) -> Result<(), Box<dyn std::error::Error>> {
    let tmp_dir = PATH_PARTY.join("tmp");
    let game_root = h.get_game_rootpath()?;
    let gamename = h.handler_dir_name().to_string();

    for (i, instance) in instances.iter().enumerate() {
        // Build lowerdir stack (leftmost has highest priority)
        let mut lowerdir_parts: Vec<String> = Vec::new();

        // 1. Goldberg overlay first (highest priority)
        if let Some(overlay) = goldberg_overlays.get(i) {
            lowerdir_parts.push(overlay.display().to_string());
        }

        // 2. Handler overlay (if exists)
        let handler_overlay = h.path_handler.join("overlay");
        if handler_overlay.exists() {
            lowerdir_parts.push(handler_overlay.display().to_string());
        }

        // 3. Base game directory (lowest priority)
        lowerdir_parts.push(game_root.clone());

        let path_lowerdir = lowerdir_parts.join(":");

        let path_game_mnt = tmp_dir.join(format!("game-{}", i));
        let path_workdir = tmp_dir.join(format!("work-{}", i));
        let path_prof = PATH_PARTY.join("profiles").join(&instance.profname);
        let path_upperdir = path_prof.join("gamesaves").join(&gamename);

        std::fs::create_dir_all(&path_game_mnt)?;
        std::fs::create_dir_all(&path_workdir)?;

        let mut cmd = Command::new("fuse-overlayfs");
        cmd.arg("-o");
        cmd.arg(format!("lowerdir={}", path_lowerdir));
        cmd.arg("-o");
        cmd.arg(format!("upperdir={}", path_upperdir.display()));
        cmd.arg("-o");
        cmd.arg(format!("workdir={}", path_workdir.display()));
        cmd.arg(&path_game_mnt);

        println!(
            "[splitux] Mounting overlay for instance {}: lowerdir={}",
            i, path_lowerdir
        );

        let status = cmd
            .status()
            .map_err(|_| "Fuse-overlayfs executable not found; Please install fuse-overlayfs through your distro's package manager.")?;
        if !status.success() {
            return Err("fuse-overlayfs mount failed.".into());
        }
    }

    Ok(())
}

